import React, { Component } from 'react';
import NotesDataService from '../service/NotesDataService';

class ListNotesComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            notes: [],
            message: null
        }
        this.refreshNotes = this.refreshNotes.bind(this)
    }

    componentDidMount() {
        this.refreshNotes();
    }

    refreshNotes() {
        NotesDataService.retrieveAllNotes()
            .then(
                response => {
                    console.log(response);
                }
            )
    }

    render() {
        return (
            <div className="container">
                <h3>All Notes</h3>
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Id</th>
                                <th>Title</th>
                                <th>Content</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.notes.map(
                                    note =>
                                        <tr key={note.id}>
                                            <td>{note.title}</td>
                                            <td>{note.content}</td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

export default ListNotesComponent
